x = []
y = []
z = []
for item in results.Y:
	x.append(item[0])
	y.append(item[1])
	z.append(item[2])

close('all') # close all latent plotting windows
fig1 = figure() # Make a plotting figure
ax = Axes3D(fig1) # use the plotting figure to create a Axis3D object.
ata = [x,y,z] 
ax.scatter(ata[0], ata[1], ata[2], 'r', mark='^') # make a scatter plot of blue dots from the data
 
# make simple, bare axis lines through space:
xAxisLine = ((min(ata[0]), max(ata[0])), (0, 0), (0,0)) # 2 points make the x-axis line at the data extrema along x-axis 
ax.plot(xAxisLine[0], xAxisLine[1], xAxisLine[2], 'r') # make a red line for the x-axis.
yAxisLine = ((0, 0), (min(ata[1]), max(ata[1])), (0,0)) # 2 points make the y-axis line at the data extrema along y-axis
ax.plot(yAxisLine[0], yAxisLine[1], yAxisLine[2], 'r') # make a red line for the y-axis.
zAxisLine = ((0, 0), (0,0), (min(ata[2]), max(ata[2]))) # 2 points make the z-axis line at the data extrema along z-axis
ax.plot(zAxisLine[0], zAxisLine[1], zAxisLine[2], 'r') # make a red line for the z-axis.
 
# label the axes 
ax.set_xlabel("PC1 - "+str(results.fracs[0]*100)+'%') 
ax.set_ylabel("PC2 - "+str(results.fracs[1]*100)+'%')
ax.set_zlabel("PC3 - "+str(results.fracs[2]*100)+'%')
ax.set_title("The title of the plot")
show() # show the plot